// Recent Photo or Avatar
// Short Biography
import React from "react";

function About() {
    return (
        <div className="mainBody">
            <p className="content is-medium">Full Stack Developer</p>
            <hr />
            <img className="my-pic" src={process.env.PUBLIC_URL + '/img/profile.jpg'} alt="Ricky Chen" />
            <p className="content is-italic mt-4">
                I'm Ricky, a web developer the full stack to better my work
            </p>
            <p className="content">
                • 2+ years of experience in developing web page and user interfaces using HTML5, CSS3, Bootstrap, JavaScript, jQuery,
                React JS, Webpack, JSON, Socket.io, MySQL, MongoDB, AJAX, DOM.
                <br></br>
                • Expertise in developing front-end web applications using client-side JavaScript frameworks like handlebar. JS and React
                JS to achieve MVC design.
                <br></br>
                • Experienced in developing applications using React JS for view rendering and implementing React JS with Redux and Flux
to achieve MVC design flow to the application.

            </p>
        </div>
    );
}

export default About;
